import React from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const Card = styled(motion.div)`
  background: ${({ theme }) => theme.cardBackground};
  border-radius: 8px;
  padding: 1rem;
  margin: 1rem;
  box-shadow: 0 4px 8px rgba(0,0,0,0.1);
  cursor: pointer;
  text-align: center;
  width: 200px; /* Adjusted width */

  img {
    border-radius: 8px;
    width: 100%;
    height: 300px; /* Fixed height for consistency */
    object-fit: cover; /* Ensure image covers the entire area */
  }

  h3 {
    margin-top: 1rem;
    font-size: 1rem; /* Adjusted font size */
  }
`;

const FavoriteButton = styled.button`
  background: ${({ theme }) => theme.headerFooter};
  color: ${({ theme }) => theme.headerFooterText};
  border: none;
  padding: 0.5rem;
  cursor: pointer;
  border-radius: 5px;
  margin-top: 0.5rem;

  &:hover {
    background: ${({ theme }) => theme.cardBackground};
  }
`;

const MovieCard = ({ movie }) => {
  const navigate = useNavigate();

  const handleClick = () => {
    sessionStorage.setItem('lastViewedMovie', JSON.stringify(movie));
    navigate(`/movie/${movie.id}`, { state: movie });
  };

  const handleFavorite = () => {
    const favorites = JSON.parse(localStorage.getItem('favorites')) || [];
    const isFavorite = favorites.find(fav => fav.id === movie.id);
    if (!isFavorite) {
      favorites.push(movie);
      localStorage.setItem('favorites', JSON.stringify(favorites));
    }
  };

  return (
    <Card 
      onClick={handleClick}
      whileHover={{ scale: 1.05 }}
      whileTap={{ scale: 0.95 }}
    >
      <img
        src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
        alt={movie.title}
      />
      <h3>{movie.title}</h3>
      <FavoriteButton onClick={(e) => {
        e.stopPropagation();
        handleFavorite();
      }}>Add to Favorites</FavoriteButton>
    </Card>
  );
};

export default MovieCard;
