import React from 'react';
import { useDarkMode } from '../hooks/useDarkMode';

const ThemeSwitcher = () => {
  const [theme, themeToggler] = useDarkMode();

  return (
    <button onClick={themeToggler}>
      Switch to {theme === 'light' ? 'Dark' : 'Light'} Theme
    </button>
  );
};

export default ThemeSwitcher;
