import React from 'react';
import { useLocation } from 'react-router-dom';
import styled from 'styled-components';

const Details = styled.div`
  background: ${({ theme }) => theme.cardBackground};
  border-radius: 8px;
  padding: 2rem;
  margin: 2rem;
  box-shadow: 0 4px 8px rgba(0,0,0,0.1);
`;

const HeroDetailsPage = () => {
  const { state: hero } = useLocation();

  return (
    <Details>
      {hero ? (
        <>
          <img src={`https://api.opendota.com${hero.img}`} alt={hero.localized_name} />
          <h1>{hero.localized_name}</h1>
          <p>Primary Attribute: {hero.primary_attr}</p>
          <p>Attack Type: {hero.attack_type}</p>
          <p>Roles: {hero.roles.join(', ')}</p>
        </>
      ) : (
        <p>Loading...</p>
      )}
    </Details>
  );
};

export default HeroDetailsPage;
