import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import TeamCard from '../components/TeamCard';
import PlayerCard from '../components/PlayerCard';

const Container = styled.div`
  padding: 2rem;
`;

const Section = styled.section`
  margin-bottom: 2rem;
`;

const Home = () => {
  const [teams, setTeams] = useState([]);
  const [players, setPlayers] = useState([]);

  useEffect(() => {
    const fetchTeams = async () => {
      const response = await axios.get('https://api.football-data.org/v2/competitions/PL/teams', {
        headers: { 'X-Auth-Token': 'YOUR_API_KEY' }
      });
      setTeams(response.data.teams);
    };

    const fetchPlayers = async () => {
      const response = await axios.get('https://api.football-data.org/v2/competitions/PL/scorers', {
        headers: { 'X-Auth-Token': 'YOUR_API_KEY' }
      });
      setPlayers(response.data.scorers.map(scorer => scorer.player));
    };

    fetchTeams();
    fetchPlayers();
  }, []);

  return (
    <Container>
      <h1>Home Page</h1>
      <Section>
        <h2>Teams</h2>
        <div className="teams">
          {teams.map(team => (
            <TeamCard key={team.id} team={team} />
          ))}
        </div>
      </Section>
      <Section>
        <h2>Players</h2>
        <div className="players">
          {players.map(player => (
            <PlayerCard key={player.id} player={player} />
          ))}
        </div>
      </Section>
    </Container>
  );
};

export default Home;
