import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import HeroCard from '../components/TeamCard';
import ItemCard from '../components/ItemCard';

const Container = styled.div`
  padding: 2rem;
`;

const Section = styled.section`
  margin-bottom: 2rem;
`;

const Home = () => {
  const [heroes, setHeroes] = useState([]);
  const [items, setItems] = useState([]);

  useEffect(() => {
    const fetchHeroes = async () => {
      const response = await axios.get('https://api.opendota.com/api/heroStats');
      setHeroes(response.data);
    };

    const fetchItems = async () => {
      const response = await axios.get('https://api.opendota.com/api/constants/items');
      setItems(Object.values(response.data));
    };

    fetchHeroes();
    fetchItems();
  }, []);

  return (
    <Container>
      <h1>Home Page</h1>
      <Section>
        <h2>Heroes</h2>
        <div className="heroes">
          {heroes.map(hero => (
            <HeroCard key={hero.id} hero={hero} />
          ))}
        </div>
      </Section>
      <Section>
        <h2>Items</h2>
        <div className="items">
          {items.map(item => (
            <ItemCard key={item.id} item={item} />
          ))}
        </div>
      </Section>
    </Container>
  );
};

export default Home;
